private void jButton2MouseClicked(java.awt.event.MouseEvent evt) {//GEN-FIRST:event_jButton2MouseClicked
    Runtime r=Runtime.getRuntime(); 
    String batFile="netsh wlan set hostednetwork mode=allow";
    String batFile1="netsh wlan set hostednetwork ssid="+jTextField1.getText();
    String batFile2="netsh wlan set hostednetwork key="+jPasswordField1.getText();

        try{
            int b = jTextField1.getText().indexOf(" ");
                 if(jTextField1.getText().equalsIgnoreCase("") || b != -1){
                 JOptionPane.showMessageDialog(null,"ท่านไม่ได้ใส่ชื่อ wifi hotspot หรือ มีช่องว่างระหว่างคำ");
            }
            if(jPasswordField1.getText().equalsIgnoreCase("")||jPasswordField1.getText().length()<8){
                JOptionPane.showMessageDialog(null,"ท่านไม่ได้ใส่รหัส หรือ ใส่น้อยกว่า 8 ตัว");
            }
            else{
            r.exec(batFile);
            r.exec(batFile1);
            r.exec(batFile2);
            closeAboutBox();
            }}catch(Exception e){
            closeAboutBox();
            System.out.println("error: "+e.getMessage()); 
            }   
}//GEN-LAST:event_jButton2MouseClicked
    
